    /**
     * Class constructor
     */
    public function __construct()
    {
        // Add shortcodes
        if (function_exists('add_shortcode')) {

            // Register shortcode
            \add_shortcode('social_media_icons', [$this, 'socialMediaIcons']);
        }
    }

    /**
     * Register a shortcode for social media icons
     * @shortcoode [social_media_icons]
     */
